import React from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity } from 'react-native';
import COLORS from '../styles/colors';
import images from '../styles/images/images';

export const CustomDrawer = ({ navigation }) => {
    return(
        <View style={styles.container}>
            <View style={styles.userInfo}>
                <Image style={styles.userImg} source={images.avatarImg} /> 
                <Text style={styles.username}>Username</Text>  
            </View>  

            <View style={styles.drawerBtnsWrapper}>
                <TouchableOpacity 
                    style={[styles.drawerBtn, styles.drawerFirstBtn]} onPress={() => navigation.navigate("CreateListScreen")} >
                    <Text style={styles.drawerBtnLabel}>Add New List</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.drawerBtn} >
                    <Text style={styles.drawerBtnLabel}>One Time List</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.drawerBtn} >
                    <Text style={styles.drawerBtnLabel}>Regular List</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.drawerBtn} >
                    <Text style={styles.drawerBtnLabel}>User Settings</Text>
                </TouchableOpacity>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    userInfo: {
        flexDirection: "row",
        marginTop: 40,
    },
    userImg: {
        marginHorizontal: 16,
        width: 50,
        height: 50,
    },
    username: {
        fontSize: 24,
        color: COLORS.dark,
        paddingTop: 5,
        paddingLeft: 22,
    },
    drawerBtnsWrapper: {
        flex: 1,
        backgroundColor: COLORS.main,
        borderTopStartRadius: 20,
        borderTopEndRadius: 20,
        marginTop: 16,
        overflow: "hidden",
    },
    drawerBtn: {  
        marginTop: 10,
        paddingHorizontal: 20,      
    },
    drawerFirstBtn: { 
        marginBottom: 21, 
        marginTop: 17, 
    },
    drawerBtnLabel: {
        paddingVertical: 7,
        backgroundColor: "white",
        textAlign: "center",
        color: COLORS.main,
        textTransform: "uppercase",
        borderRadius: 38,
        fontSize: 14,
        alignItems: "center",
        fontWeight: "bold",
    }

});
