import React, { useContext } from 'react';
import { 
    View, 
    StyleSheet, 
    TextInput, 
    TouchableOpacity, 
} from 'react-native';

import COLORS from '../styles/colors';
import { CustomText } from '../components/CustomText';
import { ShopListCTX } from '../context/list';

export const CreateListScreen = () => {

    const { newShopList, createListHandler, inputChangeHandler } = useContext(ShopListCTX);

    return(
        <View style={styles.container}>
            <View style={styles.heading}>
                <CustomText numberOfLines={1} style={styles.title}>
                    New List
                </CustomText>
                <View style={styles.form}>
                    <CustomText style={styles.listName}>List Name</CustomText>
                    <TextInput 
                        placeholder="Something for me" 
                        onChangeText={inputChangeHandler}
                        value={newShopList}
                        style={[styles.field]} 
                    />

                    <View style={styles.optionsWrapper}>
                        <TouchableOpacity style={styles.options}>
                            <CustomText style={styles.optionsLabel}>One Time</CustomText>
                        </TouchableOpacity>
                        <TouchableOpacity style={styles.options}>
                            <CustomText style={styles.optionsLabel}>Regular</CustomText>
                        </TouchableOpacity>  
                    </View>
                    
                    <TouchableOpacity onPress={createListHandler} style={styles.crateBtn}>
                        <CustomText weight="bold" style={styles.crateBtnTitle}>
                            Create List
                        </CustomText>
                    </TouchableOpacity>
                </View>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        backgroundColor: "white",
        flex: 1,
    },
    heading: {
        paddingTop: 22,
        backgroundColor: COLORS.main,
    },
    title: {
        fontSize: 18,
        color: "white",
        textAlign: "center",
        paddingVertical: 17,
    },
    form: {
        paddingTop: 10,
        borderTopStartRadius: 20,
        borderTopEndRadius: 20,
        backgroundColor: "white",
        alignItems: "center",
    },
    listName: {
        textAlign: "center",
        marginHorizontal: 10,
        paddingBottom: 10,
    },
    field: {
        backgroundColor: COLORS.grey,
        width: 380,
        borderRadius: 45,
        paddingVertical: 8,
        textAlign: "center", 
        fontWeight: "bold",
        color: "red",
        marginBottom: 15,
    }, 
    optionsWrapper: {
        flexDirection: "row",
        width: 380,
        justifyContent: "space-between",
    },
    options: {
        backgroundColor: COLORS.grey,
        paddingVertical: 13,
        paddingHorizontal: 60,
        borderRadius: 45,
        marginBottom: 15,
    },
    optionsLabel: {
        textAlign: "center",
    },
    crateBtn: {
        backgroundColor: COLORS.main,
        borderRadius: 45,
        width: 380,
    },
    crateBtnTitle: {
        color: "white",
        fontSize: 14,
        textTransform: "uppercase",
        paddingVertical: 14,
        textAlign: "center",
    }

});
