import React from 'react';
import { View, Text, StyleSheet, TextInput, TouchableOpacity } from 'react-native';

import COLORS from '../styles/colors';

export const CreateListScreen = () => {
    return(
        <View style={styles.container}>
            <View style={styles.heading}>
                <Text style={styles.title}>New List</Text>
                <View style={styles.form}>
                    <Text style={styles.listName}>List Name</Text>
                    <TextInput 
                        placeholder="Something for me" 
                        style={[styles.field, { fontWeight: "bold", color: "red",}]} 
                    />

                    <View style={styles.optionsWrapper}>
                        <TouchableOpacity style={styles.options}>
                            <Text style={styles.optionsLabel}>One Time</Text>
                        </TouchableOpacity>
                        <TouchableOpacity style={styles.options}>
                            <Text style={styles.optionsLabel}>Regular</Text>
                        </TouchableOpacity>  
                    </View>
                    
                    <TouchableOpacity style={styles.crateBtn}>
                        <Text style={styles.crateBtnTitle}>Create List</Text>
                    </TouchableOpacity>
                </View>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        backgroundColor: "white",
        flex: 1,
    },
    heading: {
        paddingTop: 22,
        backgroundColor: COLORS.main,
    },
    title: {
        fontSize: 18,
        color: "white",
        textAlign: "center",
        fontWeight: "bold",
        paddingHorizontal: 170,
        paddingVertical: 17,
    },
    form: {
        paddingTop: 10,
        borderTopStartRadius: 20,
        borderTopEndRadius: 20,
        backgroundColor: "white",
        alignItems: "center",
    },
    listName: {
        textAlign: "center",
        marginHorizontal: 10,
        paddingBottom: 10,
    },
    field: {
        backgroundColor: COLORS.grey,
        width: 380,
        borderRadius: 45,
        paddingVertical: 8,
        textAlign: "center", 
        fontWeight: "bold",
        color: "red",
        marginBottom: 15,
    }, 
    optionsWrapper: {
        flexDirection: "row",
        width: 380,
        justifyContent: "space-between",
    },
    options: {
        backgroundColor: COLORS.grey,
        paddingVertical: 13,
        paddingHorizontal: 65,
        borderRadius: 45,
        marginBottom: 15,
    },
    optionsLabel: {
        textAlign: "center",
    },
    crateBtn: {
        backgroundColor: COLORS.main,
        borderRadius: 45,
        width: 380,
    },
    crateBtnTitle: {
        color: "white",
        fontWeight: "bold",
        fontSize: 14,
        textTransform: "uppercase",
        paddingVertical: 14,
        textAlign: "center",
    }

});


