import React, {useState} from 'react';
import {Module} from './Components/Module';
import Button from './Components/Button';

import './styles.scss';

function App() {

  const [firstModalStatus, setFirstModalStatus] = useState(false);
  const [secondModalStatus, setSecondModalStatus] = useState(false);

  const toggleafirstModal = () => setFirstModalStatus (v => !v);


  return (
    <div className="App">

      <Button
        backgroundColor='rgba(0,0,0,0.2)'
        text='Open the first module window'
        onClick={toggleafirstModal}  /> 

      <Button
        backgroundColor='green'
        text='Open the second module window'
        onClick={() => alert('second modal window')}  /> 

      {firstModalStatus && (
      <Module
        header='Do you want to delete this syle?'
        text='Hello World'
        close={toggleafirstModal}
        actions={[
          <Button
          key={1}
          backgroundColor='rgba(0,0,0,0.2)'
          text='Ok'
          onClick={() => alert('first modal window')} />,

          <Button
          key={2}
          backgroundColor='rgba(0,0,0,0.2)'
          text='Cancel'
          onClick={toggleafirstModal} /> ]}  />
      )}

    </div>
  );
}

export default App;
