import React from 'react'

 export const Module = ({header, closeIcon, actions, text, close}, props) => {

    return(
        <div className="modal">
            <header>
                {header}
                {closeIcon  &&  
                    <button
                        className="close-btn"
                        onClick={close}>X
                    </button>
                }
            </header>
            <p className="modal-text">{text}</p>
            {actions}
        </div>
    )
}
