import React, {useState} from 'react';
import {Module} from './Components/Module';
import Button from './Components/Button';

import './styles.scss';

function App() {

  const [firstModalStatus, setFirstModalStatus] = useState(false);
  const [secondModalStatus, setSecondModalStatus] = useState(false);
  const [color, setColor] = useState('red');

  const toggleFirstModal = () => setFirstModalStatus (v => !v);
  const toggleSecondModal = () => setSecondModalStatus (v => !v);

  return (
    <div className="App">

      <Button
        backgroundColor='red'
        text='Open the first modal window'
        onClick={toggleFirstModal} 
      /> 

      <Button
        backgroundColor='green'
        text='Open the second modal window'
        onClick={toggleSecondModal}  
      /> 

      {firstModalStatus && (
                          <Module
                            header='Do you want to delete this file?'
                            closeIcon={true}
                            text1="Once you delete this file, it won't be possible to undo this action"
                            text2="Are you sure you want do delete it"
                            close={toggleFirstModal}
                            actions={[
                              <Button
                              key={1}
                              backgroundColor='rgba(0,0,0,0.2)'
                              text='Ok'
                              onClick={() => alert('File deleted')} />,

                              <Button
                              key={2}
                              backgroundColor='rgba(0,0,0,0.2)'
                              text='Cancel'
                              onClick={toggleFirstModal} /> ]}  />
       )}

      {seco && (
                          <Module
                            header='Do you want to delete this file?'
                            closeIcon={true}
                            text1="Once you delete this file, it won't be possible to undo this action"
                            text2="Are you sure you want do delete it"
                            close={toggleSecondModal}
                            actions={[
                              <Button
                              key={1}
                              backgroundColor='rgba(0,0,0,0.2)'
                              text='Ok'
                              onClick={() => alert('File deleted') && setColor('black')} />,

                              <Button
                              key={2}
                              backgroundColor='rgba(0,0,0,0.2)'
                              text='Cancel'
                              onClick={toggleSecondModal} /> ]}  />
      )}



    </div>
  );
}

export default App;
