import React, {useState} from 'react';
import {Module} from './Components/Module';
import Button from './Components/Button';

import {Modal} from './Components/Modal';

import './styles.scss';

function App(props) {

  const [firstModalStatus, setFirstModalStatus] = useState(false);
  const [secondModalStatus, setSecondModalStatus] = useState({isShowing: false});

  // const [status, setStatus] = useState(false);
  // const toggleModal = () => setStatus(v => !v);

  const toggleFirstModal = () => setFirstModalStatus (v => !v);
  const toggleSecondModal = () => setSecondModalStatus (v => !v);



  const [state, setState] = useState(false);
  const  openModalHandler = () => setState({ isShowing: true });
  const  closeModalHandler = () => setState({ isShowing: false })

  return (
    <div className="App">

