import React, {useEffect, useState} from 'react';

function App() {

  const [data, setData] = useState({ products: [] });
  console.log(data);

  const getData = async (url) => {
    const response = await fetch(url);
    const json = await response.json();
    setData(data => ({
      ...json,
      products: [...data.products, ...json.products]
    }));
    // console.log(data);
  };

  const loadMore = () => {
    getData(data.next)
  }


  useEffect(() => {
    getData('http://localhost:3000/');
  }, [])


  return (
    <div className="App">

      {!!data.products.length ? data.products.map(({ name, url }) => (
        <h4 key={url}>{name}</h4>
        )) :
        <h1>Loading</h1>
      }


    </div>
  );
}

export default App;
