import React, { useContext } from 'react';
import { productsContext } from './products';

export const SingleProduct = ({ match: { params: { id } } }) => {

    const productsList = useContext(productsContext);
    const product = productsList.find(item => item.id == id);

    return(
        <div>
            {product && (
                <div>
                    {/* <button onClick={() => push('/products')}>X</button> */}
                    <img border={product.color} src={product.image} alt={product.name} />
                    <h1>{product.name}</h1>
                    <h2>{product.price}</h2>
                    <p>{product.desc}</p>
                </div>
            )}
        </div>

    )
}
