import React, { useContext, useState } from 'react';
import styled from 'styled-components';

import { MdClose } from "react-icons/md";
import { NotesContext } from '../../context/notes';
import { ModalWindow } from '../../components';

export const SingleNote = ({ history: { push }, match: { params: { id } } }) => {

    const [modalStatus, setModalStatus] = useState(false);
    const toggleModal = () => setModalStatus(v => !v);
    const modalClose = () => setModalStatus(false);

    const  notes  = useContext(NotesContext);
    const note = notes.find(item => item.id == +id);
    console.log(note);

   
    return(
        <Container>
            {note &&  (  
                <Component color={note.color}>
                    <Header>
                        <Title>{note.title}</Title>
                        <Date>{note.date}</Date>
                        <CloseButton onClick={() => push('/')}><MdClose/></CloseButton>
                    </Header>
                    <Text>{note.text}</Text>
                </Component>
            )}
            
            <ButtonsContainer>
                <FunctionalButton>Edit</FunctionalButton>
                <FunctionalButton>Archive</FunctionalButton>
                <FunctionalButton onClick={toggleModal}>Delete</FunctionalButton>
            </ButtonsContainer> 

           {modalStatus && (
                <ModalWindow
                    //show={firstModalStatus}
                    closing={firstModalClose}
                    header='Do you want to delete this file?'
                    closeIcon={true}
                    text={`Once you delete this file, it won't be possible to undo this action.\n
                        Are you sure you want do delete it`}
                    close={toggleFirstModal}
                    // actions={[
                    // <Button
                    //     key={1}
                    //     backgroundColor='rgba(0,0,0,0.2)'
                    //     text='Ok'
                    //     onClick={() => alert('File deleted')}
                    // />,
                    // <Button
                    //     key={2}
                    //     backgroundColor='rgba(0,0,0,0.2)'
                    //     text='Cancel'
                    //     onClick={toggleFirstModal} 
                    // />]} 
                            />
            )}        

        </Container>
    );
};

const Container = styled.div`
    display: flex;
    margin-top: 100px;
    margin-left: 100px;
`;

const Component = styled.div`
    display: inline-block;
    background-color: ${p => p.color};
    margin-left: 17px;
    padding: 20px;
    width: 750px;
    border-radius: 10px;
    color: white;
`;

const Header = styled.div`
    display: flex;
    padding: 5px 0;
    border-bottom: 1px solid white;
    justify-content: space-between;
`;

const Title = styled.div`
    font-size: 35px;
    font-weight: bold;
`;

const Date = styled.div`
    font-size: 25px;
    font-weight: bold;
    margin-bottom: 20px;
`;

const Text = styled.p`
    text-align: center;
    font-size: 25px;
    padding: 5px;
`;

const CloseButton = styled.button`
    display: flex;
    width: 35px;
    height: 35px;
    background: red;
    color: white;
    border: none;
    top: -15px;
    right: -15px;
    font-size: 25px;
    border: 1px solid black;
`;

const ButtonsContainer = styled.div`
    display: flex;
    flex-direction: column;
    margin-left: 50px;
`;

const FunctionalButton = styled.button`
    margin-bottom: 30px;
    font-size: 25px;
    text-transform: uppercase;
    padding: 12px 40px;
    border: 1px solid black;
    border-radius: 8px;
`;
