import React, { useState } from 'react';
import styled from 'styled-components';

import { MdClose } from "react-icons/md";

export const NoteEdition = ({header, closeIcon, actions, text, close}) => {

    const [notes, setNotes] = useState([]);

  const [fields, setFields] = useState({
    title: "",
    text: "",
    color: "#d32727",
    archiveStatus: false,
    date: Date.now(),
    //...initial
  });

  const onChange = e => {
    const { name, value } = e.target;
    setFields(field => ({
      ...field,
      [name]: value
    }));
  };

  const onSubmit = e => {
    e.preventDefault();
    //createNote(fields);
  }

  const getNotes = async () => {
    try {
      const res = await fetch("http://localhost:3006/notes");
      const data = await res.json();

      setNotes(data);
    } catch (error) {
      console.log(error);
    }
  };

    return(
        <ModalContainer>
            <h1>deqfj9</h1>
            <Form onSubmit={onSubmit}>
                <Input
                placeholder="note heading"
                type="text"
                name='title'
                value={fields.title}
                onChange={onChange}
                />
                <Textarea
                placeholder="note context"
                type="text"
                name='text'
                value={fields.text}
                onChange={onChange}
                />

                <ColorOptions>
                    <h4>Color: </h4>
                    <ColorOption color="#d32727">
                        <input
                        type="radio"
                        name="color"
                        value="#d32727"
                        checked={fields.color === "#d32727"}
                        onChange={onChange}
                        />
                        <span></span>
                    </ColorOption>
                    <ColorOption color="#3a2c84">
                        <input
                        type="radio"
                        name="color"
                        value="#3a2c84"
                        checked={fields.color === "#3a2c84"}
                        onChange={onChange}
                        />
                        <span></span>
                    </ColorOption>
                    <ColorOption color="#ef8e0b">
                        <input
                        type="radio"
                        name="color"
                        value="#ef8e0b"
                        checked={fields.color === "#ef8e0b"}
                        onChange={onChange}
                        />
                        <span></span>
                    </ColorOption>
                    <ColorOption color="#516f55">
                        <input
                        type="radio"
                        name="color"
                        value="#516f55"
                        checked={fields.color === "#516f55"}
                        onChange={onChange}
                        />
                        <span></span>
                    </ColorOption>
                </ColorOptions>
                <Button>Create Note</Button>
            </Form>
        </ModalContainer>
    );
};


const ModalContainer = styled.div`
    height: 500px; 
    width: 800px;
    top: 50%;
    left: 50%;
    //margin: -131px 0 0 -258px;   
    align-items: center
    border-radius: 5px; 
    position: absolute;
    background-color: #8a8a8a;
    text-align: center;
`;

const Header = styled.header`
    background-color: red;
    justify-content: space-between;
    display: flex;
    padding: 20px 20px 20px;
    background-color: #6c6c6c;
    color: white;
    font-size: 22px;
    font-weight: 600;
    margin-bottom: 30px;
    border-radius: 5px;
`;

const ModalText = styled.p`
    justify-content: center;
    align-items: center;
    text-align: center;
    display: flex;
    color: white;
    margin-bottom: 30px;
    font-size: 18px;
    line-height: 20px;
    font-weight: 600;
`;

const CloseButton = styled.button`
    background-color: #6c6c6c;
    color: white;
    border: none;
    cursor: pointer;
    color: white;
    font-size: 30px;
`;




const Form = styled.form`
  border: 2px solid #494d9e;
  background-color: #c3c5c1;
  border-radius: 15px;
  max-width: 300px;
  margin: 30px auto;
  padding: 20px 20px;
`;

const Input = styled.input`
  margin:0 auto;
  background-color: transparent;
  color: #494d9e;
  font-weight: bold;
  display: block;
  border: 2px solid #494d9e;
  transition: all 0.3s ease;
  width: 80%;
  padding: 10px 10px;
  border-radius: 15px;

  &:focus {
    border-color: #d3347d;
    color: #d3347d;
    outline: none;
  }
`;

const Textarea = styled.textarea`
  border-radius: 15px;
  display: block;
  background-color: transparent;
  color: #494d9e;
  padding: 10px 10px;
  font-weight: bold;
  border: 2px solid #494d9e;
  height: 100px;
  width: 80%;
  margin: 20px auto;
  resize: none;

  &:focus {
    border-color: #d3347d;
    color: #d3347d;
    outline: none;
  }
`;

const Button = styled.button`
  text-transform: uppercase;
  font-weight: bold;
  background-color: #d32727;
  color: white;
  border: none;
  height: 40px;
  width: 100%;
  border-radius: 15px;
  margin: 0 auto;
`;

const ColorOptions = styled.div`
  display: flex;
  align-items: center;
  margin: 10px 0;
  h4 {
    margin: 0 25px 0 0;
  }
`;

const ColorOption = styled.label`
  input {
    display: none;
  }

  span {
    display: inline-block;
    width: 20px;
    height: 20px;
    border-radius: 20%;
    margin: 0 10px;
    background-color: ${p => p.color};
    border: 4px solid transparent;
    cursor: pointer;
  }

  input:checked + span {
    border-color: white;
  }
`;
