import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';

import { GoPlus } from "react-icons/go";
import { FiBox } from "react-icons/fi";
import { FaRegStickyNote } from "react-icons/fa";




export const Header = () => {
    return(
        <Container>
            <Logo>NotesApp</Logo>
            <NavLinkContainer>
                <StyledNavLink exact to="/">
                    <FaRegStickyNote/>Actual
                </StyledNavLink>
                <StyledNavLink to="/archive">
                    <FiBox/>Archive
                </StyledNavLink>
                <StyledNavLink to="/create">
                    <GoPlus/>Create
                </StyledNavLink>
            </NavLinkContainer>
        </Container>
    );
};


const Container = styled.header`
    background: #c3c5c1;
    padding: 20px 50px;
    display: flex;
    //justify-content: space-between;
    align-items: center;
`;

const Logo = styled.div`
    font-size: 30px;
    font-weight: bold;
    color: white;
`;

const NavLinkContainer = styled.div`
    margin-left: 350px;
    padding: 12px 0;
    background-color: white;
    border-radius: 30px;
`;


const StyledNavLink = styled(NavLink)`
    color: black;
    text-decoration: none;
    margin: 0 15px;
    padding: 10px 15px;
    min-width: 150px;
    text-align: center;
    color: #777a72;
    background-color: white;
    border-radius: 30px;
    border: 2px solid transparent;
    transition: all 0.5s ease-out;
    font-weight: bold;

    span {
        margin-right: 10px;
    }

    &:hover{
        border-color: #777a72;
        color: red;
    }

    &.active{
        //border-color: #d32727;
        background-color: #9da099;
        color: white;
    }

`;

