import React from 'react';
import styled from 'styled-components';

export const Edit = ({ history: { push }, match: { params: { id } } }) => {

    const [notes, setNotes] = useState([]);

    const [fields, setFields] = useState({
        title: "",
        text: "",
        color: "#d32727",
        // archiveStatus: false,
        // date: Date.now(),
        // ...initial
    });

    const onChange = e => {
        const { name, value } = e.target;
        setFields(field => ({
        ...field,
        [name]: value
        }));
    };

  

    const getNotes = async () => {
        try {
        const res = await fetch("http://localhost:3006/notes");
        const data = await res.json();

        setNotes(data);
        } catch (error) {
        console.log(error);
        }
    };

    const editNote = async (id, { title, text}) => {
    try {

    const res = await fetch(`http://localhost:3006/notes/${id}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({title, text})
    });

    const answer = await res.json();
    getNotes();
    console.log(answer);

        } catch (error) {
        console.log(error);
        }
    };

    useEffect(() => {
        getNotes();
    }, []);
 
    
     const onSubmit = async (e) => {
        e.preventDefault();
        //editNote(fields.id, fields);
    }

    return(
        <ModalContainer>
        <h1 style={{ textAlign: "center", color: "#494d9e" }}>Edit Data</h1>

        {fields && (  
            <Form onSubmit={(e) => onSubmit(e)}>
                <Input
                placeholder="note heading"
                type="text"
                name='title'
                value={fields.title}
                onChange={onChange}
                />
                <Textarea
                placeholder="note context"
                type="text"
                name='text'
                value={fields.text}
                onChange={onChange}
                />

            <ColorOptions>
                <h4>Color: </h4>
                <ColorOption color="#d32727">
                    <input
                    type="radio"
                    name="color"
                    value="#d32727"
                    checked={fields.color === "#d32727"}
                    onChange={onChange}
                    />
                    <span></span>
                </ColorOption>
                <ColorOption color="#3a2c84">
                    <input
                    type="radio"
                    name="color"
                    value="#3a2c84"
                    checked={fields.color === "#3a2c84"}
                    onChange={onChange}
                    />
                    <span></span>
                </ColorOption>
                <ColorOption color="#ef8e0b">
                    <input
                    type="radio"
                    name="color"
                    value="#ef8e0b"
                    checked={fields.color === "#ef8e0b"}
                    onChange={onChange}
                    />
                    <span></span>
                </ColorOption>
                <ColorOption color="#516f55">
                    <input
                    type="radio"
                    name="color"
                    value="#516f55"
                    checked={fields.color === "#516f55"}
                    onChange={onChange}
                    />
                    <span></span>
                </ColorOption>
            </ColorOptions>
            
            <Button onClick={() => editNote(id, fields)}>Edit Note</Button>
        </Form> 
        )}
    </ModalContainer>

    );
};
