import React from 'react';
import styled from 'styled-components';

import { MdClose } from "react-icons/md";

export const NoteEdition = ({header, closeIcon, actions, text, close}) => {


    
    return(
        <ModalContainer>
            <h1>deqfj9</h1>
            <Form onSubmit={onSubmit}>
                <Input
                placeholder="note heading"
                type="text"
                name='title'
                value={fields.title}
                onChange={onChange}
                />
                <Textarea
                placeholder="note context"
                type="text"
                name='text'
                value={fields.text}
                onChange={onChange}
                />

                <ColorOptions>
                    <h4>Color: </h4>
                    <ColorOption color="#d32727">
                        <input
                        type="radio"
                        name="color"
                        value="#d32727"
                        checked={fields.color === "#d32727"}
                        onChange={onChange}
                        />
                        <span></span>
                    </ColorOption>
                    <ColorOption color="#3a2c84">
                        <input
                        type="radio"
                        name="color"
                        value="#3a2c84"
                        checked={fields.color === "#3a2c84"}
                        onChange={onChange}
                        />
                        <span></span>
                    </ColorOption>
                    <ColorOption color="#ef8e0b">
                        <input
                        type="radio"
                        name="color"
                        value="#ef8e0b"
                        checked={fields.color === "#ef8e0b"}
                        onChange={onChange}
                        />
                        <span></span>
                    </ColorOption>
                    <ColorOption color="#516f55">
                        <input
                        type="radio"
                        name="color"
                        value="#516f55"
                        checked={fields.color === "#516f55"}
                        onChange={onChange}
                        />
                        <span></span>
                    </ColorOption>
                </ColorOptions>
                <Button>Create Note</Button>
            </Form>
        </ModalContainer>
    );
};


const ModalContainer = styled.div`
    height: 300px; 
    width: 520px;
    top: 50%;
    left: 50%;
    margin: -131px 0 0 -258px;   
    border-radius: 5px; 
    position: absolute;
    background-color: #8a8a8a;
    text-align: center;
`;

const Header = styled.header`
    background-color: red;
    justify-content: space-between;
    display: flex;
    padding: 20px 20px 20px;
    background-color: #6c6c6c;
    color: white;
    font-size: 22px;
    font-weight: 600;
    margin-bottom: 30px;
    border-radius: 5px;
`;

const ModalText = styled.p`
    justify-content: center;
    align-items: center;
    text-align: center;
    display: flex;
    color: white;
    margin-bottom: 30px;
    font-size: 18px;
    line-height: 20px;
    font-weight: 600;
`;

const CloseButton = styled.button`
    background-color: #6c6c6c;
    color: white;
    border: none;
    cursor: pointer;
    color: white;
    font-size: 30px;
`;
