import React from 'react';
import styled from 'styled-components';

export const Icons = ({src, alt}) => {
    return(
        <Container>
            <img src={src} alt={alt} />
        </Container>
    );
};

const Container = styled.span`
    display: inline-flex;
    justify-content: center;
    align-items: center;
    width: 30px;
    height: 30px;
    border-radius: 100%;
    text-align: center;
    background-color: #d32727;
    line-height: 30px;
    color: white

    img {
        width: 50%;
    }
`;
