import React, {useState, useEffect} from 'react';
import {AddToCart} from '.'
import star from '../img/star.png';
import star_black from '../img/star_black.png';


export const SingleProduct = ({name, italic, price, desc1, desc2}) => {

const [data, setData] = useState({ products: [] });
  console.log(data);

  const getData = async (url) => {
    const response = await fetch(url);
    const newData = await response.json();
    setData(data => ({
      ...newData,
      products: [...data.products, ...newData.products]
    }));
    console.log(data);
  };

  useEffect(() => {
    getData('./products.json')
  }, []);

    return(
        <div className="cart">
            {data.products.map(({name, url, id, image, italic, price, desc1, desc2}) => (
                <div className="cart-description">
                    <p className="product-name">
                        {name} <i className="italic">{italic}</i>
                    </p>
                    <img src={star}/>
                    <img src={star}/>
                    <img src={star}/>
                    <img src={star}/>
                    <img src={star_black}/>
                    <p className="product-description">
                        {desc1}<br/>{desc2}
                    </p>
                    <p className="price">{price}
                        <button className="add-to-cart">Add to cart</button>
                    </p>
                </div>
                ))
            }
        </div>
    )
}
