import React, {useState} from 'react';
import { Modal } from './Modal';

export const AddToCart = () => {

    const [modalStatus, setModalStatus] = useState(false);
    
    const toggleModal = () => setModalStatus (v => !v);

    return(
        <div>

            {modalStatus && (
                            <Modal
                                header='Do you want do add this product to your cart?'
                                closeIcon={true}
                                text="If you want to add thisproduct to your cart press OK\n 
                                    else press Cancel"
                                close={toggleModal}
                                actions={[
                                    <Button
                                        key={1}
                                        backgroundColor='rgba(0,0,0,0.2)'
                                        text='Ok'
                                        onClick={() => alert('File deleted')} 
                                    />,

                                    <Button
                                        key={2}
                                        backgroundColor='rgba(0,0,0,0.2)'
                                        text='Cancel'
                                        onClick={toggleFirstModal}  
                                    />
                                ]}
                            />

            )

            }
        </div>
    )
}
