import React, { useReducer } from 'react';
import { Layout } from '../../commons';

const initialState = {count: 0};

function reducer (state, {type}) {
    switch(type) {
        case 'increment':
            return {count: state.count + 1};
        case 'decrement':
            return {count: state.count - 1};
        default: 
            return state;
    }
};

